tables.table_schema:
CREATE TABLE UserInteractions (
    interaction_id UUID PRIMARY KEY,
    user_id UUID,
    message TEXT,
    timestamp TIMESTAMP
);

CREATE TABLE chatbot_users (
    user_id INT PRIMARY KEY,
    user_name VARCHAR(50) NOT NULL,
    last_interaction TIMESTAMP
);

CREATE TABLE response_templates (
    template_id INT PRIMARY KEY,
    template_text TEXT NOT NULL,
    usage_count INT DEFAULT 0
);

CREATE TABLE user_interactions (
  user_id INT PRIMARY KEY,
  interaction_text TEXT,
  interaction_timestamp TIMESTAMP
);

CREATE TABLE chatbot_responses (
  response_id INT PRIMARY KEY,
  response_text TEXT,
  response_timestamp TIMESTAMP
);

CREATE TABLE user_sentiments (
  sentiment_id INT PRIMARY KEY,
  user_id INT,
  sentiment_score FLOAT,
  sentiment_timestamp TIMESTAMP
);

CREATE TABLE chatbot_performance (
  performance_id INT PRIMARY KEY,
  response_id INT,
  performance_rating INT
);

CREATE TABLE ConversationHistory (
    user_id INT NOT NULL,
    chatbot_id INT NOT NULL,
    timestamp TIMESTAMP NOT NULL,
    message TEXT NOT NULL
);

CREATE TABLE UserPreferences (
    user_id INT NOT NULL,
    theme VARCHAR(50) NOT NULL,
    language VARCHAR(50) NOT NULL,
    timezone VARCHAR(50) NOT NULL
);

CREATE TABLE ChatbotResponses (
    chatbot_id INT NOT NULL,
    response_id INT NOT NULL,
    response TEXT NOT NULL
);

CREATE TABLE UserFeedback (
    user_id INT NOT NULL,
    feedback_id INT NOT NULL,
    feedback_type VARCHAR(50) NOT NULL,
    feedback_description TEXT NOT NULL
);

CREATE TABLE Suggestions (
    suggestion_id INT NOT NULL,
    suggestion_text TEXT NOT NULL,
    suggested_by_user_id INT NOT NULL
);
