tables.table_schema:
CREATE TABLE device (
    device_id INT PRIMARY KEY,
    device_name VARCHAR(255),
    city VARCHAR(255),
    installation_date DATE
);

CREATE TABLE device (
    device_id INT PRIMARY KEY,
    device_name TEXT,
    installation_date DATE,
    status TEXT
);

CREATE TABLE city (
    city_id INT PRIMARY KEY,
    city_name TEXT,
    population INT,
    region TEXT
);

CREATE TABLE device (
    device_id UUID PRIMARY KEY,
    device_name TEXT,
    location TEXT,
    installation_date DATE
);

CREATE TABLE city (
    city_id UUID PRIMARY KEY,
    city_name TEXT,
    population INT,
    region TEXT
);

CREATE TABLE sensor_group (
    group_id UUID PRIMARY KEY,
    group_name TEXT,
    city_id UUID REFERENCES city(city_id)
);
