tables.table_schema:
CREATE TABLE energy_devices (
    device_id INT PRIMARY KEY,
    device_name VARCHAR(50),
    power_consumption DECIMAL(10, 2)
);

CREATE TABLE power_usage (
    usage_id INT PRIMARY KEY,
    device_id INT,
    usage_date DATE,
    usage_time TIME,
    energy_consumed DECIMAL(10, 2),
    CONSTRAINT fk_device_id FOREIGN KEY (device_id) REFERENCES energy_devices(device_id)
);

CREATE TABLE Device (
	device_id INT PRIMARY KEY,
	device_name TEXT NOT NULL,
	smart_home_id INT
);

CREATE TABLE EnergyConsumption (
	consumption_id SERIAL PRIMARY KEY,
	device_id INT,
	energy_consumed DECIMAL,
	timestamp TIMESTAMP
);

CREATE TABLE SmartHome (
	smart_home_id SERIAL PRIMARY KEY,
	smart_home_name TEXT NOT NULL,
	location TEXT
);

CREATE TABLE SmartDevices (
  device_id INT PRIMARY KEY,
  device_name VARCHAR(255),
  location VARCHAR(255),
  power_consumption FLOAT
);

CREATE TABLE DeviceStatus (
  status_id INT PRIMARY KEY,
  device_id INT,
  status VARCHAR(50),
  FOREIGN KEY (device_id) REFERENCES SmartDevices(device_id)
);

CREATE TABLE smart_devices (
    device_id UUID PRIMARY KEY,
    device_name VARCHAR(50) NOT NULL,
    power_consumption FLOAT
);

CREATE TABLE energy_usage (
    usage_id UUID PRIMARY KEY,
    device_id UUID REFERENCES smart_devices(device_id),
    timestamp TIMESTAMP NOT NULL,
    energy_consumed FLOAT
);

CREATE TABLE power_optimization (
    optimization_id UUID PRIMARY KEY,
    device_id UUID REFERENCES smart_devices(device_id),
    optimization_date DATE NOT NULL,
    optimized_power_consumption FLOAT
);

CREATE TABLE usage_patterns (
    pattern_id UUID PRIMARY KEY,
    device_id UUID REFERENCES smart_devices(device_id),
    usage_pattern JSON
);

CREATE TABLE smart_homes (
    home_id UUID PRIMARY KEY,
    home_name VARCHAR(50) NOT NULL,
    address VARCHAR(100) NOT NULL
);
