tables.table_schema:
CREATE TABLE camera_footage (
  camera_id UUID PRIMARY KEY,
  footage_url TEXT,
  timestamp TIMESTAMP
);

CREATE TABLE Cameras (
    camera_id UUID PRIMARY KEY,
    location_name STRING,
    camera_type STRING,
    resolution STRING
);

CREATE TABLE CameraFootage (
    footage_id UUID PRIMARY KEY,
    camera_id UUID REFERENCES Cameras(camera_id),
    timestamp TIMESTAMP,
    footage_data BYTES
);

CREATE TABLE Incidents (
    incident_id UUID PRIMARY KEY,
    camera_id UUID REFERENCES Cameras(camera_id),
    timestamp TIMESTAMP,
    description STRING
);
