tables.table_schema:
CREATE TABLE suspicious_logs (
    log_id SERIAL PRIMARY KEY,
    log_message TEXT,
    log_timestamp TIMESTAMP
);

CREATE TABLE security_threats (
    threat_id SERIAL PRIMARY KEY,
    threat_description TEXT,
    threat_level INT
);

CREATE TABLE suspicious_logs (
    log_id SERIAL PRIMARY KEY,
    log_content TEXT,
    timestamp TIMESTAMP
);

CREATE TABLE suspicious_users (
    user_id SERIAL PRIMARY KEY,
    username VARCHAR(50) UNIQUE,
    role VARCHAR(20),
    last_login TIMESTAMP
);

CREATE TABLE suspicious_activities (
    activity_id SERIAL PRIMARY KEY,
    user_id INT REFERENCES suspicious_users(user_id),
    log_id INT REFERENCES suspicious_logs(log_id),
    activity_type VARCHAR(50)
);

CREATE TABLE suspicious_activities_logs (
    log_id INT PRIMARY KEY,
    log_timestamp TIMESTAMP,
    activity_description TEXT
);

CREATE TABLE detected_threats (
    threat_id INT PRIMARY KEY,
    threat_type TEXT,
    threat_severity INT
);

CREATE TABLE suspicious_ips (
    ip_address INET PRIMARY KEY,
    detection_timestamp TIMESTAMP,
    last_activity_timestamp TIMESTAMP
);

CREATE TABLE detected_anomalies (
    anomaly_id INT PRIMARY KEY,
    anomaly_type TEXT,
    anomaly_description TEXT
);

CREATE TABLE users (
    user_id UUID PRIMARY KEY,
    username STRING,
    email STRING,
    password STRING
);

CREATE TABLE threat_level (
    level_id UUID PRIMARY KEY,
    level_name STRING,
    description STRING
);

CREATE TABLE security_alerts (
    alert_id UUID PRIMARY KEY,
    threat_level_id UUID,
    alert_description STRING,
    alert_timestamp TIMESTAMP
);
