tables.table_schema:
CREATE TABLE threats (
    id INT PRIMARY KEY,
    name VARCHAR(255),
    description TEXT,
    severity INT
);

CREATE TABLE threat_intelligence (
    threat_id INT PRIMARY KEY,
    threat_name VARCHAR(255) NOT NULL,
    threat_description TEXT,
    threat_level INT
);

CREATE TABLE intel_sources (
    source_id INT PRIMARY KEY,
    source_name VARCHAR(255) NOT NULL,
    source_type VARCHAR(50),
    source_url TEXT
);

CREATE TABLE intel_analysis (
    analysis_id INT PRIMARY KEY,
    analysis_date DATE,
    analysis_result TEXT,
    analyst_id INT
);

CREATE TABLE intel_relations (
    relation_id INT PRIMARY KEY,
    threat_id INT,
    source_id INT,
    analysis_id INT
);

CREATE TABLE ThreatActors (
  id INT PRIMARY KEY,
  name VARCHAR(50),
  description TEXT
);

CREATE TABLE Indicators (
  id INT PRIMARY KEY,
  type VARCHAR(50),
  value VARCHAR(255),
  source VARCHAR(100)
);

CREATE TABLE Campaigns (
  id INT PRIMARY KEY,
  name VARCHAR(100),
  start_date DATE,
  end_date DATE
);

CREATE TABLE Incidents (
  id INT PRIMARY KEY,
  description TEXT,
  status VARCHAR(20),
  severity VARCHAR(20)
);

CREATE TABLE Reports (
  id INT PRIMARY KEY,
  title VARCHAR(100),
  content TEXT,
  created_at TIMESTAMP
);
