tables.table_schema:
CREATE TABLE breaches (
  id INT PRIMARY KEY,
  date TIMESTAMP,
  affected_entity TEXT,
  description TEXT
);

CREATE TABLE attackers (
  id INT PRIMARY KEY,
  name TEXT,
  organization TEXT
);

CREATE TABLE hacking_methods (
  id INT PRIMARY KEY,
  method_name TEXT,
  complexity INT,
  description TEXT
);

CREATE TABLE investigations (
  id INT PRIMARY KEY,
  start_date TIMESTAMP,
  end_date TIMESTAMP,
  result TEXT
);
