tables.table_schema:
CREATE TABLE threat_indicators(
    indicator_id UUID PRIMARY KEY,
    indicator_type VARCHAR(50),
    indicator_value TEXT,
    indicator_description TEXT
);

CREATE TABLE siem_events (
    event_id INT PRIMARY KEY,
    event_name VARCHAR(255),
    event_timestamp TIMESTAMP,
    threat_level INT
);

CREATE TABLE siem_users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50) UNIQUE,
    email VARCHAR(100),
    role VARCHAR(50)
);

CREATE TABLE threats (
    threat_id UUID PRIMARY KEY,
    threat_name VARCHAR(255) NOT NULL,
    threat_type VARCHAR(50) NOT NULL,
    threat_level INT NOT NULL
);

CREATE TABLE events (
    event_id SERIAL PRIMARY KEY,
    event_type VARCHAR(50) NOT NULL,
    event_description TEXT,
    event_time TIMESTAMP NOT NULL
);

CREATE TABLE sensors (
    sensor_id SERIAL PRIMARY KEY,
    sensor_name VARCHAR(100) NOT NULL,
    sensor_type VARCHAR(50) NOT NULL,
    sensor_location VARCHAR(255) NOT NULL
);

CREATE TABLE incidents (
    incident_id SERIAL PRIMARY KEY,
    incident_name VARCHAR(100) NOT NULL,
    incident_description TEXT,
    incident_status VARCHAR(20) NOT NULL
);
