tables.table_schema:
CREATE TABLE verified_nodes (
    node_id INT PRIMARY KEY,
    node_name STRING NOT NULL,
    ip_address STRING NOT NULL,
    is_verified BOOL
);

CREATE TABLE verified_nodes (
    node_id INT PRIMARY KEY,
    node_name VARCHAR(50) NOT NULL,
    is_verified BOOLEAN NOT NULL
);

CREATE TABLE network_participation (
    participation_id INT PRIMARY KEY,
    node_id INT NOT NULL,
    participation_date DATE NOT NULL,
    FOREIGN KEY (node_id) REFERENCES verified_nodes(node_id)
);

CREATE TABLE VerifiedNodes (
    node_id INT PRIMARY KEY,
    node_name VARCHAR(50) NOT NULL,
    public_key VARCHAR(255) NOT NULL
);

CREATE TABLE NetworkParticipants (
    participant_id INT PRIMARY KEY,
    participant_name VARCHAR(50) NOT NULL,
    node_id INT NOT NULL,
    FOREIGN KEY (node_id) REFERENCES VerifiedNodes(node_id)
);

CREATE TABLE NodeStatus (
    node_id INT PRIMARY KEY,
    status VARCHAR(20) NOT NULL,
    last_checked TIMESTAMP NOT NULL
);

CREATE TABLE verified_nodes (
    node_id INT PRIMARY KEY,
    node_name VARCHAR(50) NOT NULL,
    ip_address VARCHAR(15) NOT NULL,
    status VARCHAR(20) DEFAULT 'Active'
);

CREATE TABLE node_security (
    security_id INT PRIMARY KEY,
    node_id INT NOT NULL,
    security_level VARCHAR(20) DEFAULT 'High',
    encryption_key VARCHAR(50) NOT NULL
);
