tables.table_schema:
CREATE TABLE sentiment_analysis_data (
    id INT PRIMARY KEY,
    text_data TEXT
);

CREATE TABLE nlp_metadata (
    id INT PRIMARY KEY,
    data_id INT,
    data_language TEXT
);

CREATE TABLE Sentiments (
    id SERIAL PRIMARY KEY,
    text VARCHAR(255) NOT NULL,
    sentiment_score FLOAT
);

CREATE TABLE TextData (
    id SERIAL PRIMARY KEY,
    text VARCHAR(255) NOT NULL
);

CREATE TABLE AnalysisResults (
    id SERIAL PRIMARY KEY,
    text_id INT REFERENCES TextData(id),
    sentiment_category VARCHAR(50)
);

CREATE TABLE SentimentAnalysisData (
    id INT PRIMARY KEY,
    text TEXT,
    sentiment_score FLOAT
);

CREATE TABLE StopWords (
    id INT PRIMARY KEY,
    word TEXT
);

CREATE TABLE TokenizedText (
    id INT PRIMARY KEY,
    text_id INT,
    token TEXT
);

CREATE TABLE SentimentLexicon (
    id INT PRIMARY KEY,
    word TEXT,
    sentiment_value FLOAT
);

CREATE TABLE NLPResults (
    id INT PRIMARY KEY,
    text_id INT,
    sentiment_label TEXT,
    sentiment_score FLOAT
);
