tables.table_schema:
CREATE TABLE access_logs (
  id SERIAL PRIMARY KEY,
  timestamp TIMESTAMP,
  user_id INT,
  action VARCHAR(255)
);

CREATE TABLE data_logs (
  id SERIAL PRIMARY KEY,
  timestamp TIMESTAMP,
  data_id INT,
  action VARCHAR(255)
);

CREATE TABLE Data_Access_Log (
    log_id SERIAL PRIMARY KEY,
    date_accessed TIMESTAMP NOT NULL,
    user_id INT NOT NULL,
    accessed_table_name TEXT NOT NULL
);

CREATE TABLE Data_Modification_Log (
    log_id SERIAL PRIMARY KEY,
    date_modified TIMESTAMP NOT NULL,
    user_id INT NOT NULL,
    modified_table_name TEXT NOT NULL
);

CREATE TABLE Data_Privacy_Compliance_Log (
    log_id SERIAL PRIMARY KEY,
    date_logged TIMESTAMP NOT NULL,
    user_id INT NOT NULL,
    compliance_action TEXT NOT NULL
);

CREATE TABLE Data_Privacy_Rules (
    rule_id SERIAL PRIMARY KEY,
    rule_description TEXT NOT NULL,
    rule_type TEXT NOT NULL,
    rule_status TEXT NOT NULL
);

CREATE TABLE User_Roles (
    ID INT PRIMARY KEY,
    User_ID INT,
    Role_Name VARCHAR(50)
);
