tables.table_schema:
CREATE TABLE Pseudonymization (
    id INT PRIMARY KEY,
    original_name VARCHAR(50),
    pseudonymized_name VARCHAR(50)
);

CREATE TABLE pseudonymized_users(
  user_id INT PRIMARY KEY,
  pseudonymized_name VARCHAR(255),
  pseudonymized_email VARCHAR(255),
  pseudonymized_address VARCHAR(255)
);

CREATE TABLE pseudonymized_orders(
  order_id INT PRIMARY KEY,
  user_id INT,
  total_amount DECIMAL(10, 2),
  order_date TIMESTAMP
);

CREATE TABLE pseudonymization_users (
    user_id INT,
    pseudonymized_name VARCHAR(50),
    pseudonymized_email VARCHAR(50),
    registration_date DATE
);

CREATE TABLE pseudonymization_products (
    product_id INT,
    pseudonymized_name VARCHAR(50),
    category VARCHAR(50),
    price DECIMAL(8, 2)
);

CREATE TABLE pseudonymization_data (
    id INT PRIMARY KEY,
    name VARCHAR(50),
    email VARCHAR(100),
    phone_number VARCHAR(20)
);

CREATE TABLE pseudonymization_log (
    log_id INT PRIMARY KEY,
    timestamp TIMESTAMP,
    action VARCHAR(50),
    user_id INT
);

CREATE TABLE pseudonymization_keys (
    key_id INT PRIMARY KEY,
    pseudonym VARCHAR(50),
