tables.table_schema:
CREATE TABLE architecture_visualization_tool (
  project_id INT PRIMARY KEY,
  project_name VARCHAR(255),
  design_specifications TEXT,
  project_plan TEXT
);

CREATE TABLE project_plans (
    id INT PRIMARY KEY,
    project_name VARCHAR,
    design_specifications TEXT
);

CREATE TABLE design_specifications (
    id INT PRIMARY KEY,
    plan_id INT,
    specification_text TEXT
);

CREATE TABLE project_plans (
    project_id INT PRIMARY KEY,
    project_name VARCHAR(255),
    project_description TEXT,
    last_updated TIMESTAMP
);

CREATE TABLE designers (
    designer_id INT PRIMARY KEY,
    designer_name VARCHAR(255),
    designer_email VARCHAR(255),
    designer_phone VARCHAR(15)
);

CREATE TABLE project_teams (
    team_id INT PRIMARY KEY,
    team_name VARCHAR(255),
    team_lead INT,
    team_size INT
);

CREATE TABLE team_members (
    member_id INT PRIMARY KEY,
    member_name VARCHAR(255),
    team_id INT,
    member_role VARCHAR(50)
);
