tables.table_schema:
CREATE TABLE audio_samples (
    sample_id INT PRIMARY KEY,
    sample_name TEXT,
    sample_type TEXT,
    sample_duration FLOAT
);

CREATE TABLE compositions (
    composition_id INT PRIMARY KEY,
    composition_name TEXT,
    composition_genre TEXT,
    composition_length FLOAT
);

CREATE TABLE audio_samples (
    id UUID PRIMARY KEY,
    sample_name STRING,
    sample_file_path STRING
);

CREATE TABLE compositions (
    id UUID PRIMARY KEY,
    composition_name STRING,
    composer_name STRING,
    composition_file_path STRING
);

CREATE TABLE users (
    id UUID PRIMARY KEY,
    username STRING,
    email STRING
);

CREATE TABLE audio_samples (
  id SERIAL PRIMARY KEY,
  sample_name VARCHAR(100) NOT NULL,
  sample_url VARCHAR(255) NOT NULL
);

CREATE TABLE compositions (
  id SERIAL PRIMARY KEY,
  composition_name VARCHAR(100) NOT NULL,
  composed_by VARCHAR(100),
  created_on TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP
);

CREATE TABLE users (
  id SERIAL PRIMARY KEY,
  username VARCHAR(100) NOT NULL,
  email VARCHAR(255) NOT NULL UNIQUE,
  created_on TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP
);
