CREATE TABLE furniture_catalog (
  furniture_id INT PRIMARY KEY,
  name VARCHAR(255),
  category VARCHAR(100),
  price DECIMAL(10, 2)
);

CREATE TABLE user_preferences (
  user_id INT PRIMARY KEY,
  username VARCHAR(50),
  favorite_furniture_id INT,
  last_accessed TIMESTAMP
);

CREATE TABLE user_ratings (
  user_id INT,
  furniture_id INT,
  rating INT,
  PRIMARY KEY (user_id, furniture_id)
);

CREATE TABLE user_preferences (
  user_id INT PRIMARY KEY,
  favorite_furniture_id INT,
  preferred_category VARCHAR(50),
  CONSTRAINT fk_favorite_furniture FOREIGN KEY (favorite_furniture_id) REFERENCES furniture_catalog(furniture_id)
);

CREATE TABLE user_preferences (
  user_id SERIAL PRIMARY KEY,
  favorite_furniture_id INT,
  preferred_color VARCHAR(50),
  preferred_style VARCHAR(50)
);

CREATE TABLE users (
  user_id SERIAL PRIMARY KEY,
  username VARCHAR(50),
  email VARCHAR(100),
  role VARCHAR(50)
);
