tables.table_schema:
CREATE TABLE iot_data (
    device_id INT PRIMARY KEY,
    timestamp TIMESTAMP,
    data JSONB
);

CREATE TABLE DeviceData (
    device_id UUID PRIMARY KEY,
    data_time TIMESTAMP,
    data_value FLOAT
);

CREATE TABLE DeviceStatus (
    device_id UUID PRIMARY KEY,
    status VARCHAR
);

CREATE TABLE IoT_devices (
    device_id INT PRIMARY KEY,
    device_name VARCHAR(255),
    device_type VARCHAR(50),
    device_location VARCHAR(100)
);

CREATE TABLE IoT_data (
    data_id INT PRIMARY KEY,
    device_id INT,
    data_timestamp TIMESTAMP,
    data_value FLOAT
);

CREATE TABLE IoT_metadata (
    metadata_id INT PRIMARY KEY,
    device_id INT,
    metadata_key VARCHAR(50),
    metadata_value TEXT
);

CREATE TABLE devices (
    device_id INT PRIMARY KEY,
    device_name VARCHAR(255) NOT NULL,
    device_type VARCHAR(50) NOT NULL,
    device_location VARCHAR(100) NOT NULL
);

CREATE TABLE locations (
    location_id INT PRIMARY KEY,
    location_name VARCHAR(100) NOT NULL,
    location_description TEXT
);

CREATE TABLE devices_locations (
    device_id INT REFERENCES devices(device_id),
    location_id INT REFERENCES locations(location_id),
    PRIMARY KEY (device_id, location_id)
);
