tables.table_schema:
CREATE TABLE NetworkDevices (
    device_id INT PRIMARY KEY,
    device_name VARCHAR(50) NOT NULL,
    manufacturer VARCHAR(50) NOT NULL,
    location VARCHAR(50) NOT NULL
);

CREATE TABLE Network_Devices (
   id SERIAL PRIMARY KEY,
   device_name TEXT,
   ip_address TEXT,
   device_type TEXT
);

CREATE TABLE User_Permissions (
   id SERIAL PRIMARY KEY,
   username TEXT,
   role TEXT
);

CREATE TABLE NetworkDevices (
    device_id INT PRIMARY KEY,
    device_name VARCHAR(50),
    device_type VARCHAR(20),
    device_capacity INT
);

CREATE TABLE NetworkTrafficLogs (
    log_id INT PRIMARY KEY,
    device_id INT,
    traffic_data JSON,
    log_timestamp TIMESTAMP
);

CREATE TABLE QosPolicies (
    policy_id INT PRIMARY KEY,
    policy_name VARCHAR(50),
    min_bandwidth INT,
    max_bandwidth INT
);

CREATE TABLE NetworkOperators (
    operator_id INT PRIMARY KEY,
    operator_name VARCHAR(50),
    operator_type VARCHAR(20),
    operator_region VARCHAR(50)
);

CREATE TABLE NetworkPerformanceMetrics (
    metric_id INT PRIMARY KEY,
    device_id INT,
    metric_name VARCHAR(50),
    metric_value FLOAT
);
