tables.table_schema:
CREATE TABLE network_elements (
    element_id UUID PRIMARY KEY,
    element_name TEXT,
    element_type TEXT,
    configuration_settings JSONB
);

CREATE TABLE element_categories (
    category_id UUID PRIMARY KEY,
    category_name TEXT,
    element_type TEXT,
    FOREIGN KEY (element_type) REFERENCES network_elements(element_type)
);

CREATE TABLE 5g_network_elements (
    element_id INT PRIMARY KEY,
    element_name VARCHAR(50) NOT NULL,
    parameter_set JSONB
);

CREATE TABLE configuration_settings (
    setting_id INT PRIMARY KEY,
    setting_name VARCHAR(50) NOT NULL,
    setting_value TEXT,
    element_id INT REFERENCES 5g_network_elements(element_id)
);

CREATE TABLE parameter_values (
    parameter_id INT PRIMARY KEY,
    parameter_name VARCHAR(50) NOT NULL,
    parameter_type VARCHAR(20),
    element_id INT REFERENCES 5g_network_elements(element_id)
);

CREATE TABLE data_logs (
    log_id SERIAL PRIMARY KEY,
    log_timestamp TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    log_message TEXT,
    element_id INT REFERENCES 5g_network_elements(element_id)
);

CREATE TABLE NetworkElementConfigurations (
    element_id INT PRIMARY KEY,
    element_type VARCHAR(50),
    parameter_name VARCHAR(100),
    parameter_value VARCHAR(255)
);

CREATE TABLE NetworkElementTypes (
    type_id INT PRIMARY KEY,
    type_name VARCHAR(50)
);

CREATE TABLE ElementParameters (
    parameter_id INT PRIMARY KEY,
    parameter_name VARCHAR(100),
    data_type VARCHAR(50)
);

CREATE TABLE ElementInstances (
    instance_id INT PRIMARY KEY,
    element_id INT,
    instance_name VARCHAR(100),
    FOREIGN KEY (element_id) REFERENCES NetworkElementConfigurations(element_id)
);
