CREATE TABLE VMNetworks (
    network_id UUID PRIMARY KEY,
    vm_id UUID REFERENCES VirtualMachines(id),
    network_name VARCHAR(50),
    subnet VARCHAR(20) NOT NULL
);

CREATE TABLE virtual_machines (
    vm_id INT PRIMARY KEY,
    vm_name VARCHAR(255),
    vm_memory_gb INT,
    vm_storage_gb INT
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(255),
    user_email VARCHAR(255)
);

CREATE TABLE networks (
    network_id INT PRIMARY KEY,
    network_name VARCHAR(255),
    subnet VARCHAR(255)
);

CREATE TABLE vm_networks (
    id UUID PRIMARY KEY,
    vm_id UUID REFERENCES virtual_machines(id),
    network_name VARCHAR(50) NOT NULL,
    subnet_cidr VARCHAR(50) NOT NULL
);

CREATE TABLE vm_disks (
    id UUID PRIMARY KEY,
    vm_id UUID REFERENCES virtual_machines(id),
    disk_name VARCHAR(50) NOT NULL,
    disk_size_gb INT NOT NULL
);

CREATE TABLE vm_snapshots (
    id UUID PRIMARY KEY,
    vm_id UUID REFERENCES virtual_machines(id),
    snapshot_name VARCHAR(50) NOT NULL,
    snapshot_date TIMESTAMP NOT NULL
);

CREATE TABLE vm_configurations (
    id UUID PRIMARY KEY,
    vm_id UUID REFERENCES virtual_machines(id),
    config_key VARCHAR(50) NOT NULL,
    config_value TEXT NOT NULL
);
