tables.table_schema:
CREATE TABLE resource_utilization (
    id SERIAL PRIMARY KEY,
    resource_name TEXT NOT NULL,
    utilization DECIMAL NOT NULL,
    timestamp TIMESTAMP NOT NULL
);

CREATE TABLE billing_information (
    id SERIAL PRIMARY KEY,
    user_id INT NOT NULL,
    billing_amount DECIMAL NOT NULL,
    billing_date DATE NOT NULL
);

CREATE TABLE resource_utilization (
    id INT PRIMARY KEY,
    resource_name VARCHAR(255),
    resource_type VARCHAR(255),
    utilization DECIMAL
);

CREATE TABLE billing_information (
    id INT PRIMARY KEY,
    invoice_number VARCHAR(50),
    customer_id INT,
    billing_amount DECIMAL
);

CREATE TABLE customer_information (
    id INT PRIMARY KEY,
    customer_name VARCHAR(255),
    email VARCHAR(255),
    phone_number VARCHAR(20)
);

CREATE TABLE resource_billing (
    billing_id INT PRIMARY KEY,
    resource_id INT,
    billing_date DATE,
    amount DECIMAL(10, 2)
);

CREATE TABLE resources (
    resource_id INT PRIMARY KEY,
    resource_name VARCHAR(255),
    resource_type VARCHAR(100),
    resource_status VARCHAR(50)
);

CREATE TABLE customers (
    customer_id INT PRIMARY KEY,
    customer_name VARCHAR(255),
    customer_email VARCHAR(255),
    customer_phone VARCHAR(20)
);

CREATE TABLE resource_utilization (
    utilization_id INT PRIMARY KEY,
    resource_id INT,
    utilization_date DATE,
    utilization_hours FLOAT
);

CREATE TABLE billing_details (
    bill_id INT PRIMARY KEY,
    customer_id INT,
    bill_date DATE,
    total_amount DECIMAL(10, 2)
);

CREATE TABLE payments (
    payment_id INT PRIMARY KEY,
    bill_id INT,
    payment_date DATE,
    payment_amount DECIMAL(10, 2)
);
