tables.table_schema:
CREATE TABLE virtual_networks (
    id SERIAL PRIMARY KEY,
    name VARCHAR(50) NOT NULL,
    cidr VARCHAR(20) NOT NULL
);

CREATE TABLE subnets (
    id SERIAL PRIMARY KEY,
    network_id INT REFERENCES virtual_networks(id),
    name VARCHAR(50) NOT NULL,
    cidr VARCHAR(20) NOT NULL
);

CREATE TABLE virtual_networks (
    network_id INT PRIMARY KEY,
    network_name VARCHAR(255),
    subnet_count INT
);

CREATE TABLE subnets (
    subnet_id INT PRIMARY KEY,
    subnet_name VARCHAR(255),
    network_id INT
);

CREATE TABLE network_metadata (
    metadata_id INT PRIMARY KEY,
    network_id INT,
    subnet_id INT,
    description TEXT
);

CREATE TABLE virtual_networks (
    id UUID PRIMARY KEY,
    name STRING NOT NULL,
    description TEXT,
    created_at TIMESTAMP
);

CREATE TABLE subnets (
    id UUID PRIMARY KEY,
    subnet_cidr STRING NOT NULL,
    network_id UUID REFERENCES virtual_networks (id),
    created_at TIMESTAMP
);

CREATE TABLE device_logs (
    id UUID PRIMARY KEY,
    log_message TEXT,
    device_id UUID REFERENCES network_devices (id),
    created_at TIMESTAMP
);
