tables.table_schema:
CREATE TABLE backup_data (
    user_id INT,
    backup_id INT,
    backup_date DATE,
    backup_file_path VARCHAR(255)
);

CREATE TABLE users (
    id INT PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    email VARCHAR(100) NOT NULL
);

CREATE TABLE backups (
    backup_id INT PRIMARY KEY,
    user_id INT,
    backup_date DATE,
    backup_file VARCHAR(255)
);

CREATE TABLE user_backup_data (
    user_id UUID PRIMARY KEY,
    backup_id UUID,
    backup_data JSONB
);

CREATE TABLE backup_settings (
    user_id UUID PRIMARY KEY,
    retention_period INTERVAL,
    storage_location TEXT
);

CREATE TABLE user_accounts (
    id INT PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    email VARCHAR(100) NOT NULL
);

CREATE TABLE backups (
    backup_id INT PRIMARY KEY,
    user_id INT NOT NULL,
    backup_location VARCHAR(255) NOT NULL,
    backup_date TIMESTAMP NOT NULL
);

CREATE TABLE restore_history (
    restore_id INT PRIMARY KEY,
    backup_id INT NOT NULL,
    restore_date TIMESTAMP NOT NULL,
    restore_status VARCHAR(50) NOT NULL
);

CREATE TABLE disaster_events (
    event_id INT PRIMARY KEY,
    event_type VARCHAR(50) NOT NULL,
    event_date TIMESTAMP NOT NULL,
    event_description TEXT
);

CREATE TABLE UserBackups (
    id INT PRIMARY KEY,
    user_id INT,
    backup_path VARCHAR(255),
    backup_date DATE
);

CREATE TABLE BackupLogs (
    log_id INT PRIMARY KEY,
    backup_id INT,
    log_description TEXT,
    log_time TIMESTAMP
);

CREATE TABLE BackupSettings (
    setting_id INT PRIMARY KEY,
    user_id INT,
    backup_frequency VARCHAR(50),
    notification_email VARCHAR(255)
);

CREATE TABLE BackupStatus (
    status_id INT PRIMARY KEY,
    backup_id INT,
    is_successful BOOLEAN,
    completed_time TIMESTAMP
);

CREATE TABLE UserCredentials (
    user_id INT PRIMARY KEY,
    username VARCHAR(50),
    password VARCHAR(50),
    email VARCHAR(255)
);
