tables.table_schema:
CREATE TABLE DataReplication (
    id INT PRIMARY KEY,
    replication_technique VARCHAR(50) NOT NULL,
    replication_factor INT NOT NULL
);

CREATE TABLE HighAvailability (
    id INT PRIMARY KEY,
    ha_technology VARCHAR(50) NOT NULL,
    ha_strategy VARCHAR(50) NOT NULL
);

CREATE TABLE CloudProvider (
    id INT PRIMARY KEY,
    provider_name VARCHAR(50) NOT NULL,
    provider_region VARCHAR(50) NOT NULL
);

CREATE TABLE CloudInstance (
    id INT PRIMARY KEY,
    instance_type VARCHAR(50) NOT NULL,
    instance_status VARCHAR(50) NOT NULL
);
