CREATE TABLE performance_monitoring (
   id INT PRIMARY KEY,
   measurement_name VARCHAR(50),
   measurement_value FLOAT,
   timestamp TIMESTAMP
);

CREATE TABLE performance_monitoring_data (
  metric_id INT PRIMARY KEY,
  metric_name VARCHAR(255),
  metric_value FLOAT
);

CREATE TABLE performance_monitoring_logs (
  log_id INT PRIMARY KEY,
  timestamp TIMESTAMP,
  message TEXT
);

CREATE TABLE performance_monitoring (id INT PRIMARY KEY, server_name VARCHAR(50), cpu_usage FLOAT, memory_usage FLOAT, disk_space FLOAT);

CREATE TABLE performance_logs (log_id INT PRIMARY KEY, server_id INT, timestamp TIMESTAMP, cpu_usage FLOAT, memory_usage FLOAT, disk_space FLOAT);

CREATE TABLE server_information (server_id INT PRIMARY KEY, server_name VARCHAR(50), location VARCHAR(50), hardware VARCHAR(50));

CREATE TABLE performance_reports (report_id INT PRIMARY KEY, server_id INT, report_date DATE, cpu_usage FLOAT, memory_usage FLOAT, disk_space FLOAT);
