tables.table_schema:
CREATE TABLE cloud_environment (
  environment_id INT PRIMARY KEY,
  environment_name VARCHAR(50),
  region VARCHAR(50),
  workload_distribution VARCHAR(50)
);

CREATE TABLE query_optimization (
  query_id INT PRIMARY KEY,
  query_description VARCHAR(100),
  optimization_strategy VARCHAR(100),
  execution_time_ms INT
);

CREATE TABLE cloud_providers (
    provider_id INT PRIMARY KEY,
    provider_name VARCHAR(255) NOT NULL,
    region VARCHAR(50) NOT NULL
);

CREATE TABLE workload (
    workload_id INT PRIMARY KEY,
    vm_id INT NOT NULL,
    query_type VARCHAR(50) NOT NULL,
    execution_time_ms INT NOT NULL
);

CREATE TABLE queries (
    query_id INT PRIMARY KEY,
    query_text TEXT NOT NULL,
    execution_count INT NOT NULL,
    last_executed_at TIMESTAMP
);

CREATE TABLE cloud_computing_nodes (
    node_id INT PRIMARY KEY,
    node_name VARCHAR(255) NOT NULL,
    node_status VARCHAR(50) NOT NULL
);

CREATE TABLE computational_workloads (
    workload_id INT PRIMARY KEY,
    workload_name VARCHAR(255) NOT NULL,
    assigned_node_id INT REFERENCES cloud_computing_nodes(node_id)
);

CREATE TABLE query_executions (
    execution_id INT PRIMARY KEY,
    workload_id INT REFERENCES computational_workloads(workload_id),
    start_time TIMESTAMP NOT NULL,
    end_time TIMESTAMP NOT NULL
);

CREATE TABLE workload_metrics (
    metric_id INT PRIMARY KEY,
    workload_id INT REFERENCES computational_workloads(workload_id),
    metric_name VARCHAR(100) NOT NULL,
    metric_value DECIMAL NOT NULL
);

CREATE TABLE node_metrics (
    node_metric_id INT PRIMARY KEY,
    node_id INT REFERENCES cloud_computing_nodes(node_id),
    metric_name VARCHAR(100) NOT NULL,
    metric_value DECIMAL NOT NULL
);
