tables.table_schema:
CREATE TABLE nlp_models (
  model_id INT PRIMARY KEY,
  model_name VARCHAR(255),
  model_type VARCHAR(50),
  model_description TEXT
);

CREATE TABLE nlp_models (
    model_id INT PRIMARY KEY,
    model_name VARCHAR(255),
    model_description TEXT,
    model_version FLOAT
);

CREATE TABLE Model_Triggers (
    trigger_id INT PRIMARY KEY,
    trigger_name VARCHAR(100) NOT NULL,
    model_id INT,
    FOREIGN KEY (model_id) REFERENCES NLP_Models(model_id)
);

CREATE TABLE language_models (
    model_id INT PRIMARY KEY,
    model_name TEXT,
    model_description TEXT
);

CREATE TABLE model_parameters (
    parameter_id INT PRIMARY KEY,
    model_id INT REFERENCES language_models(model_id),
    parameter_name TEXT,
    parameter_value TEXT
);

CREATE TABLE text_data (
    text_id INT PRIMARY KEY,
    text_content TEXT,
    text_language TEXT
);

CREATE TABLE text_analysis_results (
    analysis_id INT PRIMARY KEY,
    text_id INT REFERENCES text_data(text_id),
    model_id INT REFERENCES language_models(model_id),
    sentiment_score FLOAT
);
