tables.table_schema:
CREATE TABLE SQL_Injection_Detection (
    id INT PRIMARY KEY,
    query_text TEXT
);

CREATE TABLE sql_queries (
  id INT PRIMARY KEY,
  query TEXT
);

CREATE TABLE blocked_queries (
  id INT PRIMARY KEY,
  query TEXT,
  reason TEXT
);

CREATE TABLE sql_injection_attacks (
    id INT PRIMARY KEY,
    query VARCHAR(255) NOT NULL,
    detected BOOLEAN
);

CREATE TABLE blocked_queries (
    id INT PRIMARY KEY,
    query_id INT NOT NULL,
    blocked_at TIMESTAMP,
    reason TEXT
);

CREATE TABLE query_logs (
    id INT PRIMARY KEY,
    query VARCHAR(255) NOT NULL,
    user_id INT,
    executed_at TIMESTAMP
);

CREATE TABLE whitelist_queries (
    id INT PRIMARY KEY,
    query VARCHAR(255) NOT NULL,
    added_by INT,
    added_at TIMESTAMP
);
