tables.table_schema:
CREATE TABLE threat_intelligence_feeds (
    feed_id INT PRIMARY KEY,
    feed_name VARCHAR(255),
    feed_url VARCHAR(255)
);

CREATE TABLE cyber_threats (
    threat_id INT PRIMARY KEY,
    threat_name VARCHAR(255),
    threat_description TEXT,
    threat_level INT
);

CREATE TABLE threat_intelligence_feeds (
    feed_id INT PRIMARY KEY,
    feed_name VARCHAR(255),
    feed_description TEXT,
    feed_url TEXT
);

CREATE TABLE threat_intelligence_feed (
    id INT PRIMARY KEY,
    feed_name VARCHAR(255),
    threat_type VARCHAR(100),
    threat_description TEXT
);

CREATE TABLE security_events (
    id INT PRIMARY KEY,
    event_type VARCHAR(100),
    event_time TIMESTAMP,
    event_description TEXT
);

CREATE TABLE mitigation_strategies (
    id INT PRIMARY KEY,
    threat_id INT,
    mitigation_description TEXT,
    mitigation_time TIMESTAMP
);

CREATE TABLE threat_log (
    id INT PRIMARY KEY,
    log_time TIMESTAMP,
    threat_id INT,
    log_details TEXT
);
