tables.table_schema:
CREATE TABLE feature_adoption (
    user_id UUID PRIMARY KEY,
    feature_name VARCHAR(255) NOT NULL,
    adoption_date DATE NOT NULL,
    tier_level INT
);

CREATE TABLE feature_adoption (
    user_id INT PRIMARY KEY,
    feature_id INT,
    adoption_date DATE
);

CREATE TABLE feature_adoption (
    user_id UUID PRIMARY KEY,
    feature_id UUID,
    adoption_date TIMESTAMP,
    activity_count INT
);

CREATE TABLE feature_tiers (
    tier_id UUID PRIMARY KEY,
    tier_name VARCHAR(50) NOT NULL,
    max_features INT,
    max_users INT
);

CREATE TABLE pricing_structures (
    pricing_id UUID PRIMARY KEY,
    tier_id UUID,
    monthly_price DECIMAL(10, 2),
    annual_discount DECIMAL(5, 2)
);

CREATE TABLE feature_adoption (
   customer_id INT PRIMARY KEY,
   feature_name VARCHAR(255),
   adoption_status BOOLEAN
);

CREATE TABLE pricing_tiers (
   tier_id INT PRIMARY KEY,
   tier_name VARCHAR(255),
   price DECIMAL(10,2)
);

CREATE TABLE customer_segments (
   segment_id INT PRIMARY KEY,
   segment_name VARCHAR(255)
);

CREATE TABLE adoption_history (
   customer_id INT,
   feature_name VARCHAR(255),
   adoption_date DATE
   FOREIGN KEY (customer_id) REFERENCES feature_adoption(customer_id)
);

CREATE TABLE data_metrics (
    metric_id INT PRIMARY KEY,
    metric_name VARCHAR(255),
    metric_value DECIMAL(10,2),
    timestamp TIMESTAMP
);
