tables.table_schema:
CREATE TABLE market_demand (
  product_id INT PRIMARY KEY,
  demand_factor FLOAT
);

CREATE TABLE competition (
  competitor_id INT PRIMARY KEY,
  pricing_strategy VARCHAR(255)
);

CREATE TABLE pricing_adjustments (
    id INT PRIMARY KEY,
    product_id INT,
    market_demand INT,
    competition_level INT
);

CREATE TABLE products (
    product_id INT PRIMARY KEY,
    product_name VARCHAR(255),
    base_price DECIMAL(10, 2),
    category VARCHAR(50)
);

CREATE TABLE competitors (
    competitor_id INT PRIMARY KEY,
    competitor_name VARCHAR(255),
    market_share DECIMAL(5, 2),
    product_id INT
);

CREATE TABLE pricing_adjustments (
    id UUID PRIMARY KEY,
    product_id UUID,
    adjustment_type VARCHAR(255),
    adjustment_value DECIMAL
);

CREATE TABLE market_demand (
    id UUID PRIMARY KEY,
    product_id UUID,
    demand_level INT,
    market_share DECIMAL
);

CREATE TABLE competition (
    id UUID PRIMARY KEY,
    product_id UUID,
    competitors INT,
    competitive_ranking INT
);

CREATE TABLE saas_industry (
    id UUID PRIMARY KEY,
    product_id UUID,
    industry_category VARCHAR(255),
    subscription_price DECIMAL
);

CREATE TABLE customers (
    customer_id INT PRIMARY KEY,
    customer_name VARCHAR(255),
    email VARCHAR(255),
    subscription_plan VARCHAR(50)
);

CREATE TABLE competition (
    competitor_id INT PRIMARY KEY,
    competitor_name VARCHAR(255),
    products_offered TEXT
);

CREATE TABLE market_demand (
    demand_id INT PRIMARY KEY,
    demand_date DATE,
    daily_demand INTEGER
);

CREATE TABLE pricing_history (
    price_id INT PRIMARY KEY,
    product_id INT,
    price DECIMAL(10, 2),
    date_adjusted DATE
);
