tables.table_schema:
CREATE TABLE websites_content (
    id INT PRIMARY KEY,
    title VARCHAR(100),
    content TEXT,
    website_type VARCHAR(50)
);

CREATE TABLE website_content (
    id INT PRIMARY KEY,
    title VARCHAR(255),
    content TEXT,
    published_at TIMESTAMP
);

CREATE TABLE mobile_app_content (
    id INT PRIMARY KEY,
    title VARCHAR(255),
    description VARCHAR(500),
    category VARCHAR(50)
);

CREATE TABLE websites (
    id SERIAL PRIMARY KEY,
    name VARCHAR(255) NOT NULL,
    url VARCHAR(255) NOT NULL
);

CREATE TABLE mobile_apps (
    id SERIAL PRIMARY KEY,
    name VARCHAR(255) NOT NULL,
    platform VARCHAR(50) NOT NULL,
    version FLOAT
);

CREATE TABLE content (
    id SERIAL PRIMARY KEY,
    title VARCHAR(255) NOT NULL,
    description TEXT,
    type VARCHAR(50) NOT NULL
);

CREATE TABLE website_content (
    content_id INT PRIMARY KEY,
    content_title VARCHAR(255),
    content_body TEXT,
    published_date DATE
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50),
    email VARCHAR(100) UNIQUE,
    registration_date DATE
);

CREATE TABLE mobile_app_content (
    app_content_id INT PRIMARY KEY,
    app_content_title VARCHAR(255),
    app_content_body TEXT,
    published_date DATE
);
