tables.table_schema:
CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    email VARCHAR(100) UNIQUE NOT NULL,
    created_at TIMESTAMP
);

CREATE TABLE apps (
    app_id INT PRIMARY KEY,
    app_name VARCHAR(50) NOT NULL,
    user_id INT REFERENCES users(user_id),
    created_at TIMESTAMP
);

CREATE TABLE Paas_Providers (
    provider_id SERIAL PRIMARY KEY,
    provider_name VARCHAR(50) UNIQUE,
    foundation_year INT
);

CREATE TABLE SQL_Databases (
    db_id SERIAL PRIMARY KEY,
    db_name VARCHAR(50) UNIQUE,
    db_provider_id INT,
    FOREIGN KEY (db_provider_id) REFERENCES Paas_Providers(provider_id)
);

CREATE TABLE providers (
  provider_id INT PRIMARY KEY,
  provider_name VARCHAR,
  location VARCHAR
);

CREATE TABLE databases (
  database_id INT PRIMARY KEY,
  database_name VARCHAR,
  provider_id INT,
  FOREIGN KEY (provider_id) REFERENCES providers(provider_id)
);

CREATE TABLE applications (
  application_id INT PRIMARY KEY,
  application_name VARCHAR,
  database_id INT,
  FOREIGN KEY (database_id) REFERENCES databases(database_id)
);

CREATE TABLE developers (
  developer_id INT PRIMARY KEY,
  developer_name VARCHAR
);

CREATE TABLE providers (
    provider_id INT PRIMARY KEY,
    provider_name VARCHAR(255) NOT NULL,
    provider_region VARCHAR(50) NOT NULL
);

CREATE TABLE developers (
    developer_id INT PRIMARY KEY,
    developer_name VARCHAR(255) NOT NULL
);
