tables.table_schema:
CREATE TABLE customers (
    customer_id INT PRIMARY KEY,
    customer_name VARCHAR(50),
    customer_email VARCHAR(50),
    social_media_handle VARCHAR(50)
);

CREATE TABLE sentiment_analysis (
    sentiment_id INT PRIMARY KEY,
    interaction_id INT REFERENCES interactions(interaction_id),
    sentiment_score DECIMAL(5, 2),
    sentiment_category VARCHAR(50)
);

CREATE TABLE customer_interactions (
    customer_id INT PRIMARY KEY,
    platform VARCHAR(50),
    interaction_type VARCHAR(50),
    interaction_text TEXT
);

CREATE TABLE platform_users (
    user_id INT PRIMARY KEY,
    platform_id INT,
    user_name VARCHAR(50),
    user_email VARCHAR(50)
);

CREATE TABLE platform_activity (
    activity_id INT PRIMARY KEY,
    platform_id INT,
    activity_type VARCHAR(50),
    activity_date DATETIME
);

CREATE TABLE Customers (
  customer_id INT PRIMARY KEY,
  customer_name VARCHAR(100) NOT NULL,
  customer_email VARCHAR(100) NOT NULL
);

CREATE TABLE Sentiments (
  sentiment_id INT PRIMARY KEY,
  sentiment_type VARCHAR(50) NOT NULL,
  post_id INT,
  FOREIGN KEY (post_id) REFERENCES Posts(post_id)
);
