tables.table_schema:
CREATE TABLE model_training_history (
    model_id UUID PRIMARY KEY,
    model_name VARCHAR(255) NOT NULL,
    training_date DATE NOT NULL,
    training_duration INTERVAL,
    training_accuracy FLOAT
);

CREATE TABLE performance_metrics (
    model_id UUID PRIMARY KEY,
    loss_rate FLOAT NOT NULL,
    prediction_accuracy FLOAT NOT NULL,
    validation_accuracy FLOAT
);

CREATE TABLE hyperparameters (
  id SERIAL PRIMARY KEY,
  training_id INT REFERENCES model_training(training_id),
  param_name TEXT,
  param_value FLOAT
);

CREATE TABLE model_training_history (
    model_id INT PRIMARY KEY,
    model_name TEXT,
    training_date DATE,
    accuracy FLOAT
);

CREATE TABLE dataset_details (
    dataset_id INT PRIMARY KEY,
    dataset_name TEXT,
    dataset_size INT,
    dataset_path TEXT
);
