tables.table_schema:
CREATE TABLE sentiment_data (
  id SERIAL PRIMARY KEY,
  text TEXT
);

CREATE TABLE SentimentData (
    id INT PRIMARY KEY,
    text_data TEXT
);

CREATE TABLE ModelData (
    id INT PRIMARY KEY,
    model_name VARCHAR(50),
    accuracy FLOAT
);

CREATE TABLE TextData (
    id INT PRIMARY KEY,
    text_data TEXT
);

CREATE TABLE TrainingData (
    id INT PRIMARY KEY,
    text_id INT,
    sentiment_label VARCHAR(255),
    CONSTRAINT fk_text_id FOREIGN KEY (text_id) REFERENCES TextData(id)
);

CREATE TABLE ModelPerformance (
    id INT PRIMARY KEY,
    model_name VARCHAR(255),
    accuracy FLOAT
);

CREATE TABLE training_data (
    id INT PRIMARY KEY,
    text_data TEXT
);

CREATE TABLE sentiment_labels (
    label_id INT PRIMARY KEY,
    label TEXT
);

CREATE TABLE model_parameters (
    param_id INT PRIMARY KEY,
    parameter_name TEXT,
    parameter_value FLOAT
);

CREATE TABLE model_predictions (
    prediction_id INT PRIMARY KEY,
    text_id INT,
    label_id INT,
    prediction FLOAT
);

CREATE TABLE sentiment_text_data (
    id SERIAL PRIMARY KEY,
    text_data TEXT
);

CREATE TABLE training_data (
    id SERIAL PRIMARY KEY,
    text_id INTEGER,
    label TEXT,
    FOREIGN KEY (text_id) REFERENCES sentiment_text_data(id)
);

CREATE TABLE model_parameters (
    id SERIAL PRIMARY KEY,
    model_type TEXT,
    accuracy FLOAT
);

CREATE TABLE model_results (
    id SERIAL PRIMARY KEY,
    model_id INTEGER,
    result TEXT,
    FOREIGN KEY (model_id) REFERENCES model_parameters(id)
);

CREATE TABLE sentiment_scores (
    id SERIAL PRIMARY KEY,
    text_id INTEGER,
    score FLOAT,
    FOREIGN KEY (text_id) REFERENCES sentiment_text_data(id)
);
