CREATE TABLE named_entities (
	named_entity_id UUID PRIMARY KEY,
	named_entity_name VARCHAR(255) NOT NULL,
	named_entity_type VARCHAR(50) NOT NULL,
	text_data TEXT NOT NULL
);

CREATE TABLE named_entities (
    entity_id INT PRIMARY KEY,
    entity_name TEXT,
    entity_type TEXT
);

CREATE TABLE entity_text_data (
    data_id INT PRIMARY KEY,
    entity_id INT,
    text_data TEXT,
    FOREIGN KEY (entity_id) REFERENCES named_entities(entity_id)
);

