tables.table_schema:
CREATE TABLE SpeechData (
    id INT PRIMARY KEY,
    audio_data STRING,
    text_data STRING
);

CREATE TABLE TranscriptionModels (
    model_id INT PRIMARY KEY,
    model_name STRING,
    model_description STRING
);

CREATE TABLE speech_data (
    id INT PRIMARY KEY,
    audio_data BYTEA,
    transcribed_text TEXT,
    transcription_accuracy FLOAT
);

CREATE TABLE speaker (
    speaker_id INT PRIMARY KEY,
    speaker_name TEXT,
    accent TEXT
);

CREATE TABLE speech_recognition_model (
    model_id INT PRIMARY KEY,
    model_name TEXT,
    model_accuracy FLOAT
);

CREATE TABLE model_training_data (
    training_id INT PRIMARY KEY,
    model_id INT,
    audio_data BYTEA,
    transcribed_text TEXT,
    UNIQUE(model_id, audio_data, transcribed_text)
);
