tables.table_schema:
CREATE TABLE ImageData (
    id INT PRIMARY KEY,
    image_name VARCHAR(255),
    image_data BYTEA,
    label VARCHAR(50)
);

CREATE TABLE ModelTraining (
    model_id INT PRIMARY KEY,
    model_name VARCHAR(100),
    training_data BYTEA,
    accuracy FLOAT
);

CREATE TABLE labeled_images (
    id SERIAL PRIMARY KEY,
    image_data BYTEA,
    label VARCHAR(50)
);

CREATE TABLE image_labels (
    id SERIAL PRIMARY KEY,
    label_id INT REFERENCES labeled_images(id),
    image_id INT REFERENCES labeled_images(id)
);

CREATE TABLE machine_learning_models (
    id SERIAL PRIMARY KEY,
    model_name VARCHAR(100),
    accuracy FLOAT
);

CREATE TABLE model_predictions (
    id SERIAL PRIMARY KEY,
    image_id INT REFERENCES labeled_images(id),
    model_id INT REFERENCES machine_learning_models(id),
    prediction VARCHAR(50)
);

CREATE TABLE images (
  image_id INT PRIMARY KEY,
  image_url VARCHAR(255) NOT NULL,
  label VARCHAR(50) NOT NULL
);

CREATE TABLE labels (
  label_id INT PRIMARY KEY,
  label_name VARCHAR(50) NOT NULL
);

CREATE TABLE model_training_data (
  data_id INT PRIMARY KEY,
  model_id INT NOT NULL,
  image_id INT NOT NULL,
  label_id INT NOT NULL
);

CREATE TABLE trained_models (
  model_id INT PRIMARY KEY,
  model_name VARCHAR(50) NOT NULL,
  model_type VARCHAR(50) NOT NULL
);
