CREATE TABLE ImageFeatures(
    id INT PRIMARY KEY,
    image_name VARCHAR(255),
    feature_vector FLOAT[],
    date_created TIMESTAMP
);

CREATE TABLE ImageFeatures (
    image_id INT PRIMARY KEY,
    feature_vector BYTEA
);

CREATE TABLE ImageMetadata (
    image_id INT PRIMARY KEY,
    image_name VARCHAR(255),
    image_path VARCHAR(255)
);

CREATE TABLE image_features (
    id INT PRIMARY KEY,
    image_path TEXT,
    feature_vector ARRAY FLOAT
);

CREATE TABLE image_tags (
    id INT PRIMARY KEY,
    image_id INT,
    tag TEXT
);

CREATE TABLE image_metadata (
    id INT PRIMARY KEY,
    image_id INT,
    capture_date DATE,
    location TEXT
);

CREATE TABLE ImageFeatures (
    image_id INT PRIMARY KEY,
    feature_vector BYTEA
);

CREATE TABLE ImageTags (
    image_id INT,
    tag VARCHAR(50),
    FOREIGN KEY (image_id) REFERENCES ImageFeatures(image_id)
);

CREATE TABLE ImageMetadata (
    image_id INT PRIMARY KEY,
    metadata JSONB
);

CREATE TABLE ImageAnalysisResults (
    analysis_id SERIAL PRIMARY KEY,
    image_id INT,
    analysis_result JSONB,
    FOREIGN KEY (image_id) REFERENCES ImageFeatures(image_id)
);

CREATE TABLE Images (
    image_id INT PRIMARY KEY,
    image_name VARCHAR(255),
    image_path VARCHAR(255),
    image_features BYTEA
);

CREATE TABLE Objects (
    object_id INT PRIMARY KEY,
    object_name VARCHAR(255),
    object_category VARCHAR(255),
    object_features BYTEA
);

CREATE TABLE ImageObjects (
    image_id INT,
    object_id INT,
    PRIMARY KEY (image_id, object_id),
    FOREIGN KEY (image_id) REFERENCES Images(image_id),
    FOREIGN KEY (object_id) REFERENCES Objects(object_id)
);

CREATE TABLE ImageTags (
    image_id INT,
    tag_name VARCHAR(255),
    PRIMARY KEY (image_id, tag_name),
    FOREIGN KEY (image_id) REFERENCES Images(image_id)
);

CREATE TABLE ImageMetadata (
    image_id INT,
    captured_at TIMESTAMP,
    author VARCHAR(255),
    location VARCHAR(255),
    PRIMARY KEY (image_id),
    FOREIGN KEY (image_id) REFERENCES Images(image_id)
);
