tables.table_schema:
CREATE TABLE image_gallery_images (
  image_id INT PRIMARY KEY,
  thumbnail_url TEXT,
  full_image_url TEXT
);

CREATE TABLE thumbnails (
  id INT PRIMARY KEY,
  image_name VARCHAR(50),
  thumbnail_data BYTEA
);

CREATE TABLE image_thumbnails (
    id INT PRIMARY KEY,
    thumbnail_url VARCHAR(255) NOT NULL,
    full_size_id INT
);

CREATE TABLE full_size_images (
    id INT PRIMARY KEY,
    image_url VARCHAR(255) NOT NULL,
    image_description TEXT
);

CREATE TABLE image_gallery (
    id INT PRIMARY KEY,
    thumbnail_id INT NOT NULL,
    full_size_id INT NOT NULL,
    caption TEXT
);

CREATE TABLE ImageThumbnails (
    id INT PRIMARY KEY,
    thumbnail_link VARCHAR(255) NOT NULL,
    full_size_image_id INT
);

CREATE TABLE FullSizeImages (
    id INT PRIMARY KEY,
    image_link VARCHAR(255) NOT NULL,
    thumbnail_id INT
);

CREATE TABLE Users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50) NOT NULL UNIQUE,
    email VARCHAR(100) NOT NULL
);

CREATE TABLE Thumbnails (
    thumbnail_id UUID PRIMARY KEY,
    image_id UUID NOT NULL,
    thumbnail_data BYTEA
);

CREATE TABLE FullSizeImages (
    image_id UUID PRIMARY KEY,
    full_size_data BYTEA
);

CREATE TABLE ImageMetadata (
    image_id UUID PRIMARY KEY,
    image_name TEXT NOT NULL,
    image_description TEXT
);

CREATE TABLE ImageTags (
    tag_id UUID PRIMARY KEY,
    tag_name TEXT NOT NULL
);

CREATE TABLE ImageTagMapping (
    mapping_id UUID PRIMARY KEY,
    image_id UUID NOT NULL,
    tag_id UUID NOT NULL
);
