tables.table_schema:
CREATE TABLE image_events (
    image_id INT PRIMARY KEY,
    timestamp TIMESTAMP,
    location VARCHAR(255)
);

CREATE TABLE image_timestamps (
  image_id UUID PRIMARY KEY,
  timestamp TIMESTAMP NOT NULL,
  camera_id UUID
);

CREATE TABLE location_data (
  location_id UUID PRIMARY KEY,
  timestamp TIMESTAMP NOT NULL,
  latitude DECIMAL,
  longitude DECIMAL
);

CREATE TABLE EventTimeline (id UUID PRIMARY KEY,
  event_time TIMESTAMP NOT NULL,
  event_details VARCHAR NOT NULL);

CREATE TABLE ImageTimestamps (
    id INT,
    timestamp TIMESTAMP,
    image_url VARCHAR(255)
);

CREATE TABLE ImageLocations (
    location_id INT,
    location_name VARCHAR(100),
    latitude FLOAT,
    longitude FLOAT
);

CREATE TABLE CameraSystem (
    camera_id INT,
    camera_name VARCHAR(100),
    installation_date DATE,
    location_id INT
);

CREATE TABLE images (
    image_id UUID PRIMARY KEY,
    timestamp TIMESTAMP,
    location_point GEOMETRY
);

CREATE TABLE events (
    event_id UUID PRIMARY KEY,
    event_name VARCHAR,
    timestamp TIMESTAMP,
    location_point GEOMETRY
);

CREATE TABLE cameras (
    camera_id UUID PRIMARY KEY,
    camera_name VARCHAR,
    location_point GEOMETRY
);

CREATE TABLE camera_images (
    image_id UUID REFERENCES images(image_id),
    camera_id UUID REFERENCES cameras(camera_id),
    PRIMARY KEY (image_id, camera_id)
);

CREATE TABLE event_images (
    image_id UUID REFERENCES images(image_id),
    event_id UUID REFERENCES events(event_id),
    PRIMARY KEY (image_id, event_id)
);
