tables.table_schema:
CREATE TABLE geometric_transformations (
    id INT PRIMARY KEY,
    transformation_matrix JSON,
    translation_vector JSON,
    rotation_matrix JSON
);

CREATE TABLE camera_calibrations (
    camera_id INT PRIMARY KEY,
    focal_length FLOAT,
    principal_point_x FLOAT,
    principal_point_y FLOAT
);

CREATE TABLE distortion_parameters (
    distortion_id INT PRIMARY KEY,
    k1 FLOAT,
    k2 FLOAT,
    k3 FLOAT
);

CREATE TABLE transformation_matrices (
    matrix_id INT PRIMARY KEY,
    rotation_matrix TEXT,
    translation_vector TEXT
);

CREATE TABLE image_dimensions (
    id INT PRIMARY KEY,
    image_width INT,
    image_height INT
);

CREATE TABLE calibration_results (
    id INT PRIMARY KEY,
    calibration_date DATE,
    success BOOLEAN
);

CREATE TABLE TransformationMatrix (
    id INT PRIMARY KEY,
    rotation_matrix FLOAT[],
    translation_vector FLOAT[]
);

CREATE TABLE ImageMetadata (
    id INT PRIMARY KEY,
    image_name VARCHAR(255),
    capture_timestamp TIMESTAMP,
    camera_id INT
);
