tables.table_schema:
CREATE TABLE TrainingDataSets (
    dataset_id INT PRIMARY KEY,
    dataset_name VARCHAR(255),
    data_samples INT,
    created_date DATE
);

CREATE TABLE DeepLearningModels (
    model_id INT PRIMARY KEY,
    model_name VARCHAR(255),
    model_type VARCHAR(50),
    training_dataset_id INT,
    FOREIGN KEY (training_dataset_id) REFERENCES TrainingDataSets (dataset_id)
);

CREATE TABLE TrainingDataSets (
    dataset_id UUID PRIMARY KEY,
    dataset_name VARCHAR(100),
    dataset_path TEXT,
    image_count INT
);

CREATE TABLE DeepLearningModels (
    model_id UUID PRIMARY KEY,
    model_name VARCHAR(100),
    model_framework VARCHAR(50),
    model_accuracy FLOAT
);

CREATE TABLE ModelTrainingHistory (
    history_id UUID PRIMARY KEY,
    model_id UUID,
    training_date DATE,
    epochs INT,
    validation_accuracy FLOAT
);

CREATE TABLE TrainingDataSets (
  id INT PRIMARY KEY,
  data_path VARCHAR(255) NOT NULL,
  label VARCHAR(50) NOT NULL
);

CREATE TABLE Models (
  model_id INT PRIMARY KEY,
  model_name VARCHAR(100) NOT NULL,
  layers INT NOT NULL,
  optimizer VARCHAR(50) NOT NULL
);

CREATE TABLE ImageData (
    id INT PRIMARY KEY,
    image_url VARCHAR(255),
    label VARCHAR(50),
    data_blob BYTEA
);

CREATE TABLE ModelMetadata (
    model_id INT PRIMARY KEY,
    model_name VARCHAR(100),
    trained_on TIMESTAMP,
    description TEXT
);

CREATE TABLE ModelResults (
    result_id INT PRIMARY KEY,
    model_id INT,
    result_data TEXT,
    result_timestamp TIMESTAMP
);

CREATE TABLE TrainingMetrics (
    metric_id INT PRIMARY KEY,
    model_id INT,
    loss FLOAT,
    accuracy FLOAT
);

CREATE TABLE ModelParameters (
    parameter_id INT PRIMARY KEY,
    model_id INT,
    parameter_key VARCHAR(50),
    parameter_value VARCHAR(255)
);
