tables.table_schema:
CREATE TABLE text_data_table (
    id INT PRIMARY KEY,
    text_data TEXT
);

CREATE TABLE nlp_tasks_table (
    task_id INT PRIMARY KEY,
    nlp_task_name VARCHAR(255),
    task_description TEXT
);

CREATE TABLE TextData (
  id INT PRIMARY KEY,
  text_data VARCHAR(255)
);

CREATE TABLE WordFrequency (
  word_id INT PRIMARY KEY,
  word VARCHAR(50),
  frequency INT
);

CREATE TABLE WordAnalysis (
  analysis_id INT PRIMARY KEY,
  word_id INT,
  part_of_speech VARCHAR(50),
  sentiment_score FLOAT
);
