tables.table_schema:
CREATE TABLE reward_signals (
    signal_id INT PRIMARY KEY,
    episode_id INT,
    step_number INT,
    reward_value FLOAT
);

CREATE TABLE rewards_log (
    timestamp TIMESTAMP,
    reward_value FLOAT,
    step_number INT
);

CREATE TABLE agent_training_info (
    agent_id INT,
    training_epoch INT,
    training_loss FLOAT
);

CREATE TABLE reward_signals ( 
    timestamp TIMESTAMP PRIMARY KEY, 
    reward_value FLOAT 
);

CREATE TABLE agent_logs ( 
    timestamp TIMESTAMP PRIMARY KEY, 
    agent_id UUID, 
    action VARCHAR, 
    reward_received FLOAT 
);

CREATE TABLE Rewards (
    ID INT PRIMARY KEY,
    AgentID INT,
    TimeStamp TIMESTAMP,
    RewardValue FLOAT
);

CREATE TABLE Agents (
    AgentID INT PRIMARY KEY,
    AgentName VARCHAR(255),
    AgentType VARCHAR(50),
    EnvironmentID INT
);

CREATE TABLE TrainingLogs (
    LogID INT PRIMARY KEY,
    AgentID INT,
    TimeStamp TIMESTAMP,
    LogMessage TEXT
);

CREATE TABLE Environments (
    EnvironmentID INT PRIMARY KEY,
    EnvironmentName VARCHAR(255),
    EnvironmentType VARCHAR(50),
    EnvironmentVersion VARCHAR(20)
);

CREATE TABLE rewards (
    training_id INT PRIMARY KEY,
    timestamp TIMESTAMP,
    reward_value FLOAT
);

CREATE TABLE training_episodes (
    episode_id INT PRIMARY KEY,
    training_id INT,
    episode_duration INT,
    FOREIGN KEY (training_id) REFERENCES rewards(training_id)
);
