CREATE TABLE geospatial_data (
  id INT PRIMARY KEY,
  longitude DECIMAL,
  latitude DECIMAL,
  location_name TEXT
);

CREATE TABLE location(
    id INT PRIMARY KEY,
    latitude FLOAT,
    longitude FLOAT,
    address VARCHAR(255)
);

CREATE TABLE route(
    route_id INT PRIMARY KEY,
    start_location INT,
    end_location INT,
    distance INT
);

CREATE TABLE POI(
    poi_id SERIAL PRIMARY KEY,
    name VARCHAR(255),
    description TEXT,
    location_id INT
);

CREATE TABLE user_preferences(
    user_id SERIAL PRIMARY KEY,
    favorite_location INT,
    preferred_route INT
);
