tables.table_schema:
CREATE TABLE neural_network_training_data (
    id INT PRIMARY KEY,
    input_data TEXT,
    output_data TEXT
);

CREATE TABLE neural_network_model (
    model_id INT PRIMARY KEY,
    model_name TEXT,
    layers INT
);

CREATE TABLE NeuralNetworkData (
    id INT PRIMARY KEY,
    input_data VARCHAR(255),
    output_data VARCHAR(255)
);

CREATE TABLE ModelParameters (
    id INT PRIMARY KEY,
    layer_number INT,
    parameter_name VARCHAR(255),
    parameter_value FLOAT
);

CREATE TABLE TrainingLogs (
    id INT PRIMARY KEY,
    epoch_number INT,
    training_loss FLOAT,
    validation_loss FLOAT
);

CREATE TABLE training_data (
    id INT PRIMARY KEY,
    input_data TEXT NOT NULL,
    target_data TEXT NOT NULL
);

CREATE TABLE neural_network_layers (
    layer_id INT PRIMARY KEY,
    layer_name TEXT NOT NULL,
    activation_function TEXT NOT NULL
);

CREATE TABLE training_metrics (
    metric_id INT PRIMARY KEY,
    loss FLOAT NOT NULL,
    accuracy FLOAT NOT NULL
);

CREATE TABLE training_data (
    id SERIAL PRIMARY KEY,
    input_data TEXT,
    output_data TEXT
);

CREATE TABLE layers (
    layer_id SERIAL PRIMARY KEY,
    layer_name TEXT,
    activation_function TEXT
);

CREATE TABLE neurons (
    neuron_id SERIAL PRIMARY KEY,
    neuron_name TEXT,
    layer_id INT REFERENCES layers(layer_id),
    neuron_type TEXT
);

CREATE TABLE weights (
    weight_id SERIAL PRIMARY KEY,
    neuron_id INT REFERENCES neurons(neuron_id),
    weight_value FLOAT
);

CREATE TABLE biases (
    bias_id SERIAL PRIMARY KEY,
    neuron_id INT REFERENCES neurons(neuron_id),
    bias_value FLOAT
);
