tables.table_schema:
CREATE TABLE neural_network_weights_biases (
    layer_name VARCHAR,
    weight_matrix FLOAT[],
    bias_vector FLOAT[]
);

CREATE TABLE weights_and_biases (
  layer_id INT,
  weight FLOAT,
  bias FLOAT
);

CREATE TABLE model_layers (
  layer_id INT,
  layer_name VARCHAR(255),
  activation_function VARCHAR(50)
);

CREATE TABLE neural_network_weights (
    layer_name VARCHAR(50),
    weight_matrix JSONB
);

CREATE TABLE neural_network_biases (
    layer_name VARCHAR(50),
    bias_vector JSONB
);

CREATE TABLE neural_network_parameters (
    parameter_name VARCHAR(50),
    parameter_value FLOAT
);

CREATE TABLE neural_network_weights (
    layer_id INT,
    node_id INT,
    weight FLOAT,
    bias FLOAT
);

CREATE TABLE training_data (
    example_id INT,
    input_data VARCHAR(255),
    target_data VARCHAR(255)
);

CREATE TABLE loss_history (
    iteration_id INT,
    loss_value FLOAT
);

CREATE TABLE model_parameters (
    parameter_id INT,
    parameter_name VARCHAR(50),
    parameter_value FLOAT
);
