tables.table_schema:
CREATE TABLE normalization_parameters (
    parameter_id INT PRIMARY KEY,
    parameter_name VARCHAR(255) NOT NULL,
    parameter_value DOUBLE NOT NULL
);

CREATE TABLE hidden_layers (
    layer_id INT PRIMARY KEY,
    layer_name VARCHAR(255) NOT NULL,
    activation_function VARCHAR(50) NOT NULL
);

CREATE TABLE training_data (
    data_id INT PRIMARY KEY,
    input_data VARCHAR(255) NOT NULL,
    output_data VARCHAR(255) NOT NULL
);

CREATE TABLE BatchNormalizationLayer (
    layer_id UUID PRIMARY KEY,
    layer_name VARCHAR(255),
    normalization_factor FLOAT
);

CREATE TABLE LayerWeights (
    weight_id UUID PRIMARY KEY,
    layer_id UUID,
    weight_value FLOAT,
    FOREIGN KEY (layer_id) REFERENCES BatchNormalizationLayer(layer_id)
);

CREATE TABLE NetworkConfigurations (
    config_id UUID PRIMARY KEY,
    learning_rate FLOAT,
    dropout_rate FLOAT
);

CREATE TABLE TrainingData (
    data_id UUID PRIMARY KEY,
    feature_vector JSONB,
    output_vector JSONB
);

CREATE TABLE training_data (
    id SERIAL PRIMARY KEY,
    data_name VARCHAR(50) NOT NULL,
    data_type VARCHAR(50) NOT NULL,
    data_values TEXT
);

CREATE TABLE results (
    id SERIAL PRIMARY KEY,
    result_name VARCHAR(50) NOT NULL,
    result_type VARCHAR(50) NOT NULL,
    result_value FLOAT NOT NULL
);
