tables.table_schema:
CREATE TABLE inventory_sales (
    product_id INT,
    product_name VARCHAR(255),
    sales_date DATE,
    sales_quantity INT
);

CREATE TABLE inventory_forecast (
    product_id INT,
    product_name VARCHAR(255),
    forecast_date DATE,
    forecast_quantity INT
);

CREATE TABLE sales_data (
  transaction_id INT PRIMARY KEY,
  product_id INT,
  sale_date DATE,
  sale_amount DECIMAL
);

CREATE TABLE inventory_data (
  product_id INT PRIMARY KEY,
  stock_level INT,
  last_update_date DATE
);

CREATE TABLE forecasting_data (
  product_id INT PRIMARY KEY,
  forecast_date DATE,
  forecasted_amount DECIMAL
);
